    private void LimpiarFormulario(){
        txtCorreoUsuario.setText("");
        txtNombre.setText("");
        txtApellido.setText("");
        txtPwd.setText("");
        txtPwdVerificacion.setText("");
        txtDpi.setText("");
        txtFecha.setText("dd/mm/aaaa");
        txtTelefono.setText("");
        txtAlias.setText("");
        ComboBoxNacionalidad.setSelectedIndex(0);
        comboBoxRol.setSelectedIndex(0);
        comboBoxGenero.setSelectedIndex(0);
    }
    
    
     
    
    
